import React, { useState } from 'react';
import { FileText, Upload, BarChart3, Calendar, AlertCircle, CheckCircle, Mail, TrendingUp, TrendingDown, MessageCircle } from 'lucide-react';
import { Button } from './ui/Button';
import MedicalScatterChart from './MedicalScatterChart';
import MedicalDataVisualization from './MedicalDataVisualization';
import PlainLanguageExplanation from './PlainLanguageExplanation';

const MedicalReportsSimple = ({ patientId, patientData }) => {
  const [activeTab, setActiveTab] = useState('upload');
  const [uploadResult, setUploadResult] = useState(null);
  const [uploading, setUploading] = useState(false);

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setUploading(true);
    setUploadResult(null);

    // Simulate upload and processing
    try {
      const formData = new FormData();
      formData.append('report', file);
      formData.append('patientId', patientId);
      formData.append('reportType', 'medical_report');

      const response = await fetch('http://localhost:5000/api/reports/upload', {
        method: 'POST',
        body: formData
      });

      const result = await response.json();
      
      if (result.success) {
        setUploadResult(result.data);
        setActiveTab('summary');
      } else {
        alert('Upload failed: ' + result.error);
      }
    } catch (error) {
      console.error('Upload error:', error);
      alert('Upload failed: ' + error.message);
    } finally {
      setUploading(false);
    }
  };

  const renderUploadTab = () => (
    <div className="space-y-6">
      <div className="text-center">
        <div className="w-16 h-16 bg-gradient-to-br from-blue-100 to-blue-200 rounded-full flex items-center justify-center mx-auto mb-4 shadow-lg">
          <Upload className="w-8 h-8 text-blue-600" />
        </div>
        <h2 className="text-2xl font-bold text-stone-900 mb-2">Upload Medical Report</h2>
        <p className="text-stone-600 mb-6 max-w-md mx-auto">
          Upload PDF files, medical images, or text reports for intelligent AI analysis and insights
        </p>
      </div>

      <div className="bg-gradient-to-br from-white to-blue-50 rounded-xl p-8 border-2 border-dashed border-blue-300 text-center hover:border-blue-400 hover:shadow-lg transition-all duration-300 group">
        <div className="group-hover:scale-110 transition-transform duration-300">
          <Upload className="w-16 h-16 mx-auto mb-4 text-blue-400 group-hover:text-blue-500" />
        </div>
        <p className="text-xl font-semibold mb-2 text-stone-900">
          Choose a medical report to upload
        </p>
        <p className="text-sm text-stone-500 mb-6 max-w-sm mx-auto">
          Supports PDF, Images (PNG, JPG, TIFF), and text files up to 10MB. 
          Your data is processed securely with enterprise-grade encryption.
        </p>
        <input
          type="file"
          id="fileUpload"
          className="hidden"
          accept=".pdf,.png,.jpg,.jpeg,.tiff,.txt"
          onChange={handleFileUpload}
          disabled={uploading}
        />
        <label htmlFor="fileUpload">
          <Button
            disabled={uploading}
            className="bg-gradient-to-r from-blue-500 to-blue-600 hover:from-blue-600 hover:to-blue-700 text-white px-8 py-3 text-lg font-semibold shadow-lg hover:shadow-xl transition-all duration-300"
          >
            {uploading ? (
              <>
                <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-3"></div>
                Processing...
              </>
            ) : (
              <>
                <Upload className="w-5 h-5 mr-3" />
                Select File
              </>
            )}
          </Button>
        </label>
      </div>

      {uploading && (
        <div className="bg-gradient-to-r from-blue-50 to-indigo-50 rounded-xl p-6 border border-blue-200 shadow-lg animate-pulse">
          <div className="flex items-center gap-4 mb-4">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
            <div className="flex-1">
              <span className="font-semibold text-lg text-blue-900">Processing your report...</span>
              <div className="text-sm text-blue-700 mt-1">
                This may take a few moments while we extract text and analyze with AI
              </div>
            </div>
          </div>
          <div className="bg-white rounded-lg p-3">
            <div className="flex items-center justify-between text-sm">
              <span className="text-stone-600">Analysis Progress</span>
              <span className="text-blue-600 font-medium">Processing...</span>
            </div>
            <div className="mt-2 bg-blue-100 rounded-full h-2">
              <div className="bg-gradient-to-r from-blue-500 to-indigo-500 h-2 rounded-full animate-pulse" style={{width: '65%'}}></div>
            </div>
          </div>
        </div>
      )}

      {/* Features Overview */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <div className="bg-white rounded-lg p-4 shadow-sm border border-stone-200">
          <div className="w-8 h-8 bg-green-100 rounded-lg flex items-center justify-center mb-3">
            <CheckCircle className="w-4 h-4 text-green-600" />
          </div>
          <h3 className="font-semibold text-stone-900 mb-1">OCR Processing</h3>
          <p className="text-sm text-stone-600">Advanced text extraction from any document type</p>
        </div>
        <div className="bg-white rounded-lg p-4 shadow-sm border border-stone-200">
          <div className="w-8 h-8 bg-blue-100 rounded-lg flex items-center justify-center mb-3">
            <FileText className="w-4 h-4 text-blue-600" />
          </div>
          <h3 className="font-semibold text-stone-900 mb-1">AI Analysis</h3>
          <p className="text-sm text-stone-600">Intelligent medical insights powered by Gemini</p>
        </div>
        <div className="bg-white rounded-lg p-4 shadow-sm border border-stone-200">
          <div className="w-8 h-8 bg-purple-100 rounded-lg flex items-center justify-center mb-3">
            <BarChart3 className="w-4 h-4 text-purple-600" />
          </div>
          <h3 className="font-semibold text-stone-900 mb-1">Data Visualization</h3>
          <p className="text-sm text-stone-600">Interactive charts and trend analysis</p>
        </div>
      </div>
    </div>
  );

  const renderSummaryTab = () => {
    if (!uploadResult) {
      return (
        <div className="text-center py-12">
          <FileText className="w-16 h-16 text-stone-400 mx-auto mb-4" />
          <p className="text-stone-600">No reports uploaded yet</p>
          <Button 
            onClick={() => setActiveTab('upload')}
            className="mt-4 bg-amber-600 hover:bg-amber-700 text-white"
          >
            Upload Your First Report
          </Button>
        </div>
      );
    }

    const { medicalSummary, fileName, processedAt } = uploadResult;

    return (
      <div className="space-y-6">
        <div className="bg-white rounded-xl p-6 shadow-sm border border-stone-200">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-xl font-bold text-stone-900">Report Summary</h2>
            <div className="flex gap-2">
              <Button
                size="sm"
                className="bg-blue-600 hover:bg-blue-700 text-white"
                onClick={() => setActiveTab('visualize')}
              >
                <BarChart3 className="w-4 h-4 mr-2" />
                View Charts
              </Button>
              <Button
                size="sm"
                className="bg-blue-600 hover:bg-blue-700 text-white"
                onClick={() => alert('Email functionality requires configuration in backend/.env')}
              >
                <Mail className="w-4 h-4 mr-2" />
                Send to Doctor
              </Button>
            </div>
          </div>
          
          <div className="mb-4">
            <div className="flex items-center gap-2 text-sm text-stone-600 mb-2">
              <FileText className="w-4 h-4" />
              <span>{fileName}</span>
              <span>•</span>
              <span>{new Date(processedAt).toLocaleString()}</span>
            </div>
          </div>

          {medicalSummary && (
            <div className="space-y-4">
              {medicalSummary.urgentFindings && medicalSummary.urgentFindings.length > 0 && (
                <div className="bg-red-50 border border-red-200 rounded-lg p-4">
                  <div className="flex items-center gap-2 mb-2">
                    <AlertCircle className="w-5 h-5 text-red-600" />
                    <strong className="text-red-800">Urgent Findings</strong>
                  </div>
                  <ul className="list-disc list-inside text-red-700">
                    {medicalSummary.urgentFindings.map((finding, idx) => (
                      <li key={idx}>{finding}</li>
                    ))}
                  </ul>
                </div>
              )}

              {medicalSummary.plainLanguageExplanation && (
                <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                  <h4 className="font-semibold mb-2 text-blue-900">Plain Language Summary</h4>
                  <p className="text-blue-800">{medicalSummary.plainLanguageExplanation}</p>
                </div>
              )}

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {medicalSummary.diagnoses && medicalSummary.diagnoses.length > 0 && (
                  <div className="bg-gray-50 rounded-lg p-4">
                    <h4 className="font-semibold mb-2">Diagnoses</h4>
                    <ul className="space-y-1">
                      {medicalSummary.diagnoses.map((diagnosis, idx) => (
                        <li key={idx} className="flex items-center gap-2">
                          <div className="w-2 h-2 bg-blue-500 rounded-full" />
                          <span className="text-sm">{diagnosis}</span>
                        </li>
                      ))}
                    </ul>
                  </div>
                )}

                {medicalSummary.riskFactors && medicalSummary.riskFactors.length > 0 && (
                  <div className="bg-gray-50 rounded-lg p-4">
                    <h4 className="font-semibold mb-2">Risk Factors</h4>
                    <ul className="space-y-1">
                      {medicalSummary.riskFactors.map((factor, idx) => (
                        <li key={idx} className="flex items-center gap-2">
                          <div className="w-2 h-2 bg-red-500 rounded-full" />
                          <span className="text-sm">{factor}</span>
                        </li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            </div>
          )}
        </div>

        <div className="bg-green-50 border border-green-200 rounded-lg p-4">
          <div className="flex items-center gap-2 mb-2">
            <CheckCircle className="w-5 h-5 text-green-600" />
            <strong className="text-green-800">Report Processed Successfully</strong>
          </div>
          <p className="text-green-700 text-sm">
            Your medical report has been analyzed with AI. You can now share this summary with your healthcare provider.
          </p>
        </div>
      </div>
    );
  };

  const renderBookingTab = () => (
    <div className="bg-white rounded-xl p-6 shadow-sm border border-stone-200">
      <h2 className="text-xl font-bold text-stone-900 mb-4">Book Appointment</h2>
      <p className="text-stone-600 mb-6">
        Schedule a consultation with one of our pancreatic cancer specialists
      </p>
      
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        {[
          { name: 'Dr. Sarah Chen', specialty: 'Oncology', subSpecialty: 'Pancreatic Cancer' },
          { name: 'Dr. Michael Rodriguez', specialty: 'Gastroenterology', subSpecialty: 'Pancreatic Disorders' },
          { name: 'Dr. Lisa Wang', specialty: 'Internal Medicine', subSpecialty: 'Preventive Care' }
        ].map((doctor, idx) => (
          <div key={idx} className="border border-stone-200 rounded-lg p-4 hover:bg-stone-50 cursor-pointer">
            <h3 className="font-semibold">{doctor.name}</h3>
            <p className="text-sm text-stone-600">{doctor.specialty}</p>
            <p className="text-xs text-amber-600">{doctor.subSpecialty}</p>
          </div>
        ))}
      </div>

      <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
        <p className="text-blue-800 text-sm">
          <strong>Note:</strong> Full appointment booking functionality requires additional configuration. 
          This is a simplified demo version. The complete system includes calendar integration, 
          email confirmations, and real-time availability.
        </p>
      </div>
    </div>
  );

  const renderVisualizeTab = () => {
    return (
      <div>
        <MedicalDataVisualization
          patientData={patientData}
          reportData={uploadResult}
          timeRange="30d"
        />
      </div>
    );
  };

  const renderExplainTab = () => {
    return (
      <div>
        <PlainLanguageExplanation
          reportData={uploadResult}
          patientData={patientData}
        />
      </div>
    );
  };

  return (
    <div className="space-y-6">
      {/* Header Card */}
      <div className="bg-gradient-to-r from-blue-50 to-indigo-50 rounded-xl p-6 shadow-sm border border-blue-200">
        <div className="flex items-center gap-4 mb-4">
          <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-indigo-600 rounded-xl flex items-center justify-center shadow-lg">
            <FileText className="w-6 h-6 text-white" />
          </div>
          <div className="flex-1">
            <h1 className="text-2xl font-bold text-stone-900 mb-1">Medical Report Management</h1>
            <p className="text-stone-600">
              Upload, analyze, and visualize your medical reports with AI-powered insights
            </p>
          </div>
        </div>

        {/* Tab Navigation */}
        <div className="flex gap-2 bg-white rounded-lg p-1 shadow-inner">
          <Button
            onClick={() => setActiveTab('upload')}
            variant={activeTab === 'upload' ? 'default' : 'ghost'}
            className={`flex-1 transition-all duration-200 ${
              activeTab === 'upload' 
                ? 'bg-blue-500 text-white shadow-md' 
                : 'text-stone-600 hover:text-blue-600 hover:bg-blue-50'
            }`}
          >
            <Upload className="w-4 h-4 mr-2" />
            Upload
          </Button>
          <Button
            onClick={() => setActiveTab('summary')}
            variant={activeTab === 'summary' ? 'default' : 'ghost'}
            className={`flex-1 transition-all duration-200 ${
              activeTab === 'summary'
                ? 'bg-green-500 text-white shadow-md'
                : 'text-stone-600 hover:text-green-600 hover:bg-green-50'
            }`}
          >
            <FileText className="w-4 h-4 mr-2" />
            Summary
          </Button>
          <Button
            onClick={() => setActiveTab('explain')}
            variant={activeTab === 'explain' ? 'default' : 'ghost'}
            className={`flex-1 transition-all duration-200 ${
              activeTab === 'explain'
                ? 'bg-teal-500 text-white shadow-md'
                : 'text-stone-600 hover:text-teal-600 hover:bg-teal-50'
            }`}
          >
            <MessageCircle className="w-4 h-4 mr-2" />
            Explain
          </Button>
          <Button
            onClick={() => setActiveTab('visualize')}
            variant={activeTab === 'visualize' ? 'default' : 'ghost'}
            className={`flex-1 transition-all duration-200 ${
              activeTab === 'visualize' 
                ? 'bg-purple-500 text-white shadow-md' 
                : 'text-stone-600 hover:text-purple-600 hover:bg-purple-50'
            }`}
          >
            <BarChart3 className="w-4 h-4 mr-2" />
            Visualize
          </Button>
          <Button
            onClick={() => setActiveTab('booking')}
            variant={activeTab === 'booking' ? 'default' : 'ghost'}
            className={`flex-1 transition-all duration-200 ${
              activeTab === 'booking' 
                ? 'bg-amber-500 text-white shadow-md' 
                : 'text-stone-600 hover:text-amber-600 hover:bg-amber-50'
            }`}
          >
            <Calendar className="w-4 h-4 mr-2" />
            Book Appointment
          </Button>
        </div>
      </div>

      {/* Tab Content */}
      {activeTab === 'upload' && renderUploadTab()}
      {activeTab === 'summary' && renderSummaryTab()}
      {activeTab === 'explain' && renderExplainTab()}
      {activeTab === 'visualize' && renderVisualizeTab()}
      {activeTab === 'booking' && renderBookingTab()}
    </div>
  );
};

export default MedicalReportsSimple;
